
public class fomattest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		   String[] x8 = {"apple","banana","grapes","apple","grapes"};



		   int c = 0, c1=0, c2=0;

		   for (int i=0; i<x8.length; i++)

		   {

		   if(x8[i] =="apple")

		   {

		   c++;

		   }

		   if(x8[i] =="banana")

		   {

		   c1++;

		   }

		   if(x8[i] =="grapes")

		   {

		   c2++;

		   }



		   

		   System.out.println("apple" +c);

		   System.out.println("banana" +c1);

		   System.out.println("grapes" +c2);
		   
	}
	}
}

